mapper.select:
SELECT
, ( SELECT p.name FROM provinces p WHERE p.id = cc.cust_province ) AS provinceName, ( SELECT c.name FROM cities c WHERE c.id = cc.cust_city ) AS cityName, ( SELECT cust.cust_name FROM crm_customer cust, crm_rele_cust ccc WHERE cust.id = ccc.cust_id AND ccc.target_id = cc.id AND ccc.rele_type = 1
AND cust.cust_name LIKE '%${page.params.contacts}%'
AND cust.cust_phone LIKE '%${page.params.contactsPhone}%'
AND cust.cust_qq LIKE '%${page.params.contactsQQ}%'
LIMIT 0, 1 ) AS contacts, (SELECT count(ccall.id) FROM crm_callback ccall WHERE ccall.rele_type = 1 AND ccall.target_id = cc.id) as optionStatus, (SELECT ccb.call_bargain FROM crm_callback ccb WHERE ccb.target_id = cc.id AND ccb.rele_type = 1 AND ccb.is_del = 0 ORDER BY ccb.create_time DESC limit 0,1 ) AS callBargain, (SELECT ccb.call_time FROM crm_callback ccb WHERE ccb.target_id = cc.id AND ccb.rele_type = 1 AND ccb.is_del = 0 ORDER BY ccb.call_time DESC limit 0,1 ) AS callTime, (SELECT ccb.call_next_time FROM crm_callback ccb WHERE ccb.target_id = cc.id AND ccb.rele_type = 1 AND ccb.is_del = 0 ORDER BY ccb.call_time DESC limit 0,1 ) AS callNextTime, ( SELECT count(DISTINCT(ccc.id)) FROM crm_customer ccc, crm_rele_cust crcc, crm_customer ccc2, crm_rele_cust crcc2 WHERE ccc2.id = crcc2.cust_id AND crcc2.rele_type = 1 AND crcc2.target_id = cc.id AND ccc.id = crcc.cust_id AND crcc.rele_type = 1 AND ccc.cust_phone = ccc2.cust_phone AND crcc.target_id != cc.id ) AS isRepeat FROM crm_clue cc WHERE 1=1
AND cc.cust_name LIKE '%${page.params.custName}%'
AND cc.clue_status_online = #{page.params.clueStatusOnline,jdbcType=TINYINT}
AND cc.cust_province = #{page.params.custProvince,jdbcType=INTEGER}
AND cc.cust_city = #{page.params.custCity,jdbcType=INTEGER}
AND cc.clue_type = #{page.params.clueType,jdbcType=TINYINT}
AND cc.clue_source = #{page.params.clueSource,jdbcType=TINYINT}
AND cc.clue_know = #{page.params.clueKnow,jdbcType=TINYINT}
AND cc.owner_name LIKE '%${page.params.ownerName}%'
AND EXISTS ( SELECT cust.cust_name FROM crm_customer cust, crm_rele_cust ccc WHERE cust.id = ccc.cust_id AND ccc.target_id = cc.id AND ccc.rele_type = 1
AND cust.cust_name LIKE '%${page.params.contacts}%'
AND cust.cust_phone LIKE '%${page.params.contactsPhone}%'
LIMIT 0, 1 )
AND EXISTS ( SELECT cp.id FROM crm_product cp, crm_clue_product ccp WHERE cp.id = ccp.product_id AND ccp.clue_id = cc.id AND cp.id = #{page.params.productId,jdbcType=BIGINT} AND ccp.is_del = 0 )
AND cc.create_time >= #{page.params.createTimestart,jdbcType=TIMESTAMP}
AND cc.create_time <= #{page.params.createTimeend,jdbcType=TIMESTAMP}
AND cc.allot_time >= #{page.params.allotTimestart,jdbcType=TIMESTAMP}
AND cc.allot_time <= #{page.params.allotTimeend,jdbcType=TIMESTAMP}
AND cc.clue_status_online != 0
AND cc.owner = #{page.params.owner,jdbcType=BIGINT}
AND cc.owner = #{page.params.userId,jdbcType=BIGINT}
AND cc.admin_id = #{page.params.adminId,jdbcType=BIGINT}
AND cc.owner IN
#{ow}
AND cc.owner = #{page.params.owner,jdbcType=BIGINT}
AND cc.owner = #{page.params.owner,jdbcType=BIGINT}
AND cc.admin_id = #{page.params.adminId,jdbcType=BIGINT}
AND cc.owner = #{page.params.owner,jdbcType=BIGINT}
AND cc.admin_id IN
#{ow}
AND cc.is_del = 0
HAVING ( 1=1
AND `optionStatus`=#{page.params.optionStatus,jdbcType=TINYINT}
AND `optionStatus`>=#{page.params.optionStatus,jdbcType=TINYINT}
AND `callBargain`=#{page.params.callBargain,jdbcType=TINYINT}
AND `callNextTime`>=#{page.params.nextTimestart,jdbcType=TIMESTAMP}
AND `callNextTime`<=#{page.params.nextTimeend,jdbcType=TIMESTAMP}
AND `callTime`>=#{page.params.callTimestart,jdbcType=TIMESTAMP}
AND `callTime`<=#{page.params.callTimeend,jdbcType=TIMESTAMP}
AND contacts IS NOT NULL
)
ORDER BY cc.create_time DESC
